package Patterns1and2;

import java.util.Scanner;

/**
 * @author virat
 *
 */
public class SquarePattern {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 Scanner sc = new Scanner(System.in);
	        int n = sc.nextInt();
	        for(int i=0;i<n;i++){
	            for(int j=0;j<n;j++)
	            {
	                System.out.print(n);
	            }
	            System.out.println();
	        }

	}

}
